'use strict';

/** @hidden */
/* eslint-disable array-element-newline */
const BOOLEAN_ATTRIBUTES = [
    "allowfullscreen",
    "async",
    "autofocus",
    "autoplay",
    "checked",
    "compact",
    "controls",
    "declare",
    "default",
    "defaultchecked",
    "defaultmuted",
    "defaultselected",
    "defer",
    "disabled",
    "draggable",
    "enabled",
    "formnovalidate",
    "hidden",
    "indeterminate",
    "inert",
    "ismap",
    "itemscope",
    "loop",
    "multiple",
    "muted",
    "nohref",
    "nomodule",
    "noresize",
    "noshade",
    "novalidate",
    "nowrap",
    "open",
    "pauseonexit",
    "playsinline",
    "readonly",
    "required",
    "reversed",
    "scoped",
    "seamless",
    "selected",
    "sortable",
    "spellcheck",
    "translate",
    "truespeed",
    "typemustmatch",
    "visible"
];
/**
 * Check if the attribute does not accept or requires a value.
 */
function is_boolean_attribute(attribute) {
    return BOOLEAN_ATTRIBUTES.indexOf(attribute.name.chars.toLowerCase()) >= 0;
}

exports.BOOLEAN_ATTRIBUTES = BOOLEAN_ATTRIBUTES;
exports.is_boolean_attribute = is_boolean_attribute;
